
const { ipcRenderer } = require('electron')
ipcRenderer.on('discord', (event, type, ...args) => {
  switch (type) {
    case 'ready':
      discordOnReady(...args)
      break
    case 'disconnected':
      discordOnDisconnected(...args)
      break
    case 'tick':
      discordOnTick(...args)
      break
  }
})

function discordOnReady(...args) {
  console.log('discordOnReady')
}
function discordOnDisconnected(...args) {
  console.log(`discordOnDisconnected ${args[0]}${args[1]}`)
}
function discordOnTick(...args) {
  console.log('discordOnTick')
}

